const i=document.querySelectorAll(".progress-bar"),c=document.getElementById("contact-form"),o=document.getElementById("toast-success"),r=document.querySelectorAll(".text-field"),l=document.getElementById("dismiss-toast"),n=document.getElementById("main-header"),a=document.getElementById("hire-me-btn"),d=document.getElementById("legend-text");window.addEventListener("scroll",()=>{scrollY>=126?n.style.background="#2a1236":(n.style.background="transparent",n.style.backgroundSize="cover",n.style.backgroundPosition="top")});const m=new IntersectionObserver(t=>{t.forEach(e=>{if(e.isIntersecting){const s=e.target.dataset.value;e.target.style.width=`${s}%`}else e.target.style.width="0%"})});i.forEach(t=>{m.observe(t)});function u(t){document.querySelectorAll(".nav-link").forEach(s=>s.classList.remove("active-nav")),t.currentTarget.classList.add("active-nav")}const v=document.querySelectorAll(".nav-link");v.forEach(t=>{t.addEventListener("click",u)});a.addEventListener("click",()=>{d.textContent="Hire me"});l.addEventListener("click",()=>{o.style.visibility="hidden",o.classList.remove("perform-slide")});c.addEventListener("submit",t=>{t.preventDefault(),fetch(c.action,{method:"POST",body:new FormData(c)}).then(e=>e.json()).then(e=>{o.style.visibility="visible",o.classList.add("perform-slide"),r.forEach(s=>{s.value=""})}).catch(e=>console.log(e))});
