const r=document.querySelectorAll(".progress-bar"),c=document.getElementById("contact-form"),n=document.getElementById("toast-success"),i=document.querySelectorAll(".text-field"),l=document.getElementById("dismiss-toast"),o=document.getElementById("main-header"),a=document.getElementById("hire-me-btn"),d=document.getElementById("legend-text");document.querySelectorAll(".project-image");const m=document.getElementById("hero-title"),u=document.querySelectorAll(".hero-sub");window.addEventListener("scroll",()=>{scrollY>=126?o.style.background="#2a1236":(o.style.background="transparent",o.style.backgroundSize="cover",o.style.backgroundPosition="top")});const v=new IntersectionObserver(t=>{t.forEach(e=>{if(e.isIntersecting){const s=e.target.dataset.value;e.target.style.width=`${s}%`}else e.target.style.width="0%"})}),g=new IntersectionObserver(t=>{t.forEach(e=>{e.isIntersecting?e.target.classList.add("neon-effect"):e.target.classList.remove("neon-effect")})}),h=new IntersectionObserver(t=>{t.forEach(e=>{e.isIntersecting?e.target.classList.add("hero-sub"):e.target.classList.remove("hero-sub")})});r.forEach(t=>{v.observe(t)});g.observe(m);u.forEach(t=>{h.observe(t)});function b(t){document.querySelectorAll(".nav-link").forEach(s=>s.classList.remove("active-nav")),t.currentTarget.classList.add("active-nav")}const f=document.querySelectorAll(".nav-link");f.forEach(t=>{t.addEventListener("click",b)});a.addEventListener("click",()=>{d.textContent="Ready for you"});l.addEventListener("click",()=>{n.style.visibility="hidden",n.classList.remove("perform-slide")});c.addEventListener("submit",t=>{t.preventDefault(),fetch(c.action,{method:"POST",body:new FormData(c)}).then(e=>e.json()).then(e=>{n.style.visibility="visible",n.classList.add("perform-slide"),i.forEach(s=>{s.value=""})}).catch(e=>console.log(e))});
