const a=document.querySelectorAll(".progress-bar"),c=document.getElementById("contact-form"),o=document.getElementById("toast-success"),r=document.querySelectorAll(".text-field"),i=document.getElementById("dismiss-toast"),n=document.getElementById("main-header");window.addEventListener("scroll",()=>{scrollY>=126?n.style.background="#2a1236":(n.style.background="transparent",n.style.backgroundSize="cover",n.style.backgroundPosition="top")});const l=new IntersectionObserver(t=>{t.forEach(e=>{if(e.isIntersecting){const s=e.target.dataset.value;e.target.style.width=`${s}%`}else e.target.style.width="0%"})});a.forEach(t=>{l.observe(t)});function d(t){document.querySelectorAll(".nav-link").forEach(s=>s.classList.remove("active-nav")),t.currentTarget.classList.add("active-nav")}const m=document.querySelectorAll(".nav-link");m.forEach(t=>{t.addEventListener("click",d)});i.addEventListener("click",()=>{o.style.visibility="hidden",o.classList.remove("perform-slide")});c.addEventListener("submit",t=>{t.preventDefault(),fetch(c.action,{method:"POST",body:new FormData(c)}).then(e=>e.json()).then(e=>{o.style.visibility="visible",o.classList.add("perform-slide"),r.forEach(s=>{s.value=""})}).catch(e=>console.log(e))});
