const r=document.querySelectorAll(".progress-bar"),c=document.getElementById("contact-form"),o=document.getElementById("toast-success"),a=document.querySelectorAll(".text-field"),i=document.getElementById("dismiss-toast"),s=document.getElementById("main-header");window.addEventListener("scroll",()=>{scrollY>=126?s.style.background="#2a1236":(s.style.background="transparent",s.style.backgroundSize="cover",s.style.backgroundPosition="top")});const d=new IntersectionObserver(t=>{t.forEach(e=>{if(e.isIntersecting){const n=e.target.dataset.value;e.target.style.width=`${n}%`}})});r.forEach(t=>{d.observe(t)});i.addEventListener("click",()=>{o.style.visibility="hidden",o.classList.remove("perform-slide")});c.addEventListener("submit",t=>{t.preventDefault(),fetch(c.action,{method:"POST",body:new FormData(c)}).then(e=>e.json()).then(e=>{o.style.visibility="visible",o.classList.add("perform-slide"),a.forEach(n=>{n.value=""})}).catch(e=>console.log(e))});
